<template>
  <div id="main">
    <v-combobox
      v-model="chips"
      :items="items"
      label="List of Categories"
      chips
      clearable
      prepend-icon="add"
      solo
      multiple
      @input="insertLabel()"
    >
      <template slot="selection" slot-scope="data">
        <v-chip :selected="data.selected" close @input="remove(data.item)">
          <v-avatar class="grey darken-3 white--text" v-text="(data.index)+1"></v-avatar>
          <strong>{{ data.item }}</strong>&nbsp;
          <!-- <span>(interest)</span> -->
        </v-chip>
      </template>
    </v-combobox>

    <section
      v-if="show"
      class="dataspace"
      v-html="actualContent[index]"
      style="font-size: 2.5em;"
      @click="getIndex(actualContent[index], $event)"
    ></section>

    <div id="highlight_menu" style="display:none;">
      <ul class="side-by-side">
        <li v-for="(data, index) in chips" :key="data" @click="getString(data)">
          <a href="#" :title="data">
            <img :src="icons[index]">
          </a>
        </li>
        <!-- 
        <li id="first-menu">
          <a href="#" title="Skill" class="menuItem" @click="getString">
            <img
              src="data:image/svg+xml;utf8;base64,"
            >
          </a>
        </li>
        <li id="second-menu">
          <a href="#" title="Location">
            <img
              src="data:image/png;base64,"
            >
          </a>
        </li>
        <li id="third-menu">
          <a href="#" title="Job Title">
            <img
              src="data:image/png;base64,"
            >
          </a>
        </li>
        <li id="fourth-menu">
          <a href="#" title="Irrelevant">
            <img
              src="data:image/png;base64,"
            >
          </a>
        </li>-->
      </ul>

      <div class="caret"></div>
    </div>
    <v-btn id="btn" class="success" @click="submitData(), updateComponent()">Next</v-btn>
  </div>
</template>

<script>
import { mainHighlighter } from "@/assets/js/highlight.js";
import axios from "axios";
//import md5 from "md5";

export default {
  props: {
    contents: Array,
    file: String
  },
  mounted() {
    fetch("http://localhost:8081/label")
      .then(response => response.json())
      //.then(data => console.log(JSON.stringify(data)))
      .then(data => {
        for (let i = 0; i < data.length; i++) {
          if (data[i].type == "contextual") {
            this.chips.push(data[i].label);
          }
        }
      });
  },
  updated() {
    mainHighlighter();
  },
  data() {
    return {
      show: true,
      finalContents: this.contents,
      index: 0,
      indexer: 0,
      tempdata: null,
      tempstring: "",
      actualContent: [],
      chips: [],
      items: ["Streaming", "Eating"],
      selected: ["John"],
      icons: [
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,",
        "data:image/svg+xml;utf8;base64,"
      ]
    };
  },
  // watch: {
  //   contents(val) {
  //     this.finalContents = val;
  //     this.updateComponent();
  //   }
  // },
  watch: {
    contents: {
      immediate: true,
      handler: function() {
        this.index = 0;
        this.actualContent = [];
        this.parser(this.contents, "$");
        this.updateComponent();
      }
    }
  },
  methods: {
    updateComponent() {
      var self = this;
      self.show = false;
      this.$nextTick(function() {
        self.show = true;
      });
    },
    remove(item) {
      this.chips.splice(this.chips.indexOf(item), 1);
      this.chips = [...this.chips];
    },
    parser(data, delimiter) {
      this.actualContent = data.split(delimiter);
    },
    submitData() {
      this.index += 1;
      if (this.index === this.actualContent.length - 1) {
        alert("Reached end of file");
        return;
      }
    },
    insertLabel() {
      axios.post("http://localhost:8081/label", {
        label: this.chips[this.chips.length - 1],
        type: "contextual"
      });
    },
    getIndex(str, event) {
      //alert(str);
      this.tempdata = event.target.toString();
      //this.indexer = str.indexOf(event.target);
    },
    alertMe() {
      alert("hi");
    },
    getString(currentLabel) {
      setTimeout(function() {
        fetch("http://localhost:8081/text")
          .then(response => response.json())
          //.then(data => console.log(JSON.stringify(data)))
          .then(data => (this.tempstring = data[data.length - 1].text));
      }, 100);
      setTimeout(function() {
        axios.post("http://localhost:8081/labeledcontent", {
          label: currentLabel,
          type: "contextual",
          file_id: this.file,
          content_id: this.tempstring
        });
      }, 200);
    }
  }
};
</script>

